contracts/interfaces/ISafeToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "./ISafeVault.sol";

/// @title  ISafeToken
/// @author crypt0grapher
/// @notice This contract is used as a token
interface ISafeToken is IERC20, IERC20Metadata {

    /**
    *   @notice buy SAFE tokens for given amount of USD, taxes deducted from the provided amount, SAFE is minted
    *   @param _usdToSpend number of tokens to buy, the respective amount of USD will be deducted from the user, Safe Yield token will be minted
    */
    function buySafeForExactAmountOfUSD(uint256 _usdToSpend) external returns (uint256);

    /**
    *   @notice calculate and deduct amount of USD needed to buy given amount of SAFE tokens, SAFE is minted
    *   @param _safeTokensToBuy number of tokens to buy, the respective amount of stable coin  will be deducted from the user, Safe Yield token will be minted
    */
    function buyExactAmountOfSafe(uint256 _safeTokensToBuy) external;

    /**
    *   @notice sell given amount of SAFE tokens for USD, taxes deducted from the user, SAFE is burned
    *   @param _safeTokensToSell number of tokens to sell, the respective amount of stable coin  will be returned from the user, Safe Yield token will be burned
    */
    function sellExactAmountOfSafe(uint256 _safeTokensToSell) external returns (uint256);

    /**
    *   @notice calculate the amount of SAFE needed to swap to get the exact USD amount, SAFE is burned USD redeemed from the vault
    *   @param _usdToGet number of tokens to buy, the respective amount of stable coin  will be deducted from the user, Safe Yield token will be minted
    */
    function sellSafeForExactAmountOfUSD(uint256 _usdToGet) external;

    /**
    *   @notice admin function, currently used only to deposit 1 SAFE token to the Safe Vault to set the start price
    */
    function mint(address usr, uint256 wad) external;

    /**
    *   @notice admin function
    */
    function burn(address usr, uint256 wad) external;

    /**
    *   @notice list of wallets participating in tax distribution on top of the vault
    */
    function getWallets() external view returns (address[2] memory);

    /**
    *   @notice stable coin token contract used in the protocol
    */
    function usd() external view returns (IERC20);

    /**
*   @notice attached safe vault contract
    */
    function safeVault() external view returns (ISafeVault);

    /**
    *   @notice price of 1 Safe Yield token in StableCoin
    */
    function price() external view returns (uint256);
}

contracts/interfaces/ISafeVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.17;

/// @title  ISafeVault
/// @author crypt0grapher
/// @notice Safe Yield Vault depositing to the third-party yield farms
interface ISafeVault {
    event Deposit(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);

    // @notice deposit stable coin  to the vault from the sender, anyone can deposit funds to the vault
    // @param _amount amount of stable coin  to deposit
    function deposit(uint256 _amount) external;

    // @notice Withdraw stable coin from the vault, supported only for the SafeToken currently on selling SAFE Token
    // @dev that's called remove specifically to avoid confusion with withdraw since the vault is not a pool and can't be withdrawn from except the safe token
    // @param _user user to send tokens to, withdrawn from the sender
    // @param _amount amount of stable coin  to withdraw
    function remove(address _user, uint256 _amount) external;

    // @notice totalSupply of the vault, total amount of the stablecoin in the vault, including deposits and other tokens transferred to the vault
    // @return total supply of the vault
    function totalSupply() external view returns (uint256);

    // @notice total deposited of the vault from non-Safe Token "external" users
    // @return total stable coin deposited to the vault
    function totalDeposited() external view returns (uint256);

}

