pragma solidity >=0.8.17;

/// @title  Safe NFT
/// @author crypt0grapher
/// @notice Safe Yields NFT token based on ERC1155 standard, id [0..3] represents one of the 4 tiers

contract Owned {
    /// @dev this is getting the owner from the proxy contract, the proxy contract is ERC-173 compliant and support transferOwnership
    /// @dev service function used by children contracts which are at least SafeVault.sol and SafeToken.sol
    function _getOwner() internal view returns (address adminAddress) {
        // solhint-disable-next-line security/no-inline-assembly
        assembly {
            adminAddress := sload(0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103)
        }
    }
}

contracts/SafeRouter.sol.content:
/// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "hardhat-deploy/solc_0.8/proxy/Proxied.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./Owned.sol";
import "./interfaces/ISafeToken.sol";

contract SafeRouter is Owned, Proxied, ReentrancyGuard {
    address public kyberSwapRouterContract;
    ISafeToken public safeTokenContract;
    bytes4 public allowedFunctionSignature = 0xe21fd0e9;
    IERC20 public usd;

    function initialize(address _allowedContract, address _usd, address _safeToken) public proxied {
        kyberSwapRouterContract = _allowedContract;
        usd = IERC20(_usd);
        safeTokenContract = ISafeToken(_safeToken);
        usd.approve(address(safeTokenContract), type(uint256).max);
        safeTokenContract.approve(address(safeTokenContract), type(uint256).max);
    }

    constructor(address _allowedContract, address _usd, address _safeToken) {
        initialize(_allowedContract, _usd, _safeToken);
    }

    function updateAllowedContract(address _allowedContract) external {
        require(msg.sender == tx.origin, "Only EOA can update the allowed contract");
        kyberSwapRouterContract = _allowedContract;
    }

    function updateAllowedFunctionSignature(bytes4 _allowedFunctionSignature) external {
        require(msg.sender == tx.origin, "Only EOA can update the allowed function signature");
        allowedFunctionSignature = _allowedFunctionSignature;
    }

    function proxyAndBuy(bytes calldata data) external nonReentrant {
        bytes4 functionSignature = extractFunctionSignature(data);
        require(functionSignature == allowedFunctionSignature, "Not allowed function");
        (bool success, bytes memory returnData) = kyberSwapRouterContract.delegatecall(data);
        require(success, "KyberSwap execution failed");
        (uint256 returnAmount, uint256 gasUsed) = abi.decode(returnData, (uint256, uint256));
        uint safeTokensToBuy = safeTokenContract.buySafeForExactAmountOfUSD(returnAmount);
        safeTokenContract.transfer(msg.sender, safeTokensToBuy);
    }

    function sellAndProxy(bytes calldata data, uint256 safeAmount) external nonReentrant {
        bytes4 functionSignature = extractFunctionSignature(data);
        require(functionSignature == allowedFunctionSignature, "Not allowed function");
        uint currentUSD = usd.balanceOf(address(this));
        uint usdToReturn = safeTokenContract.sellExactAmountOfSafe(safeAmount);
        (bool success, bytes memory returnData) = kyberSwapRouterContract.delegatecall(data);
        require(success, "KyberSwap execution failed");
        (uint256 returnAmount, uint256 gasUsed) = abi.decode(returnData, (uint256, uint256));
        uint remainingUsd = usd.balanceOf(address(this));
        if (remainingUsd > currentUSD) {
            usd.transfer(msg.sender, remainingUsd - currentUSD);
        }
    }

    function extractFunctionSignature(bytes memory data) internal pure returns (bytes4) {
        require(data.length >= 4, "Invalid data length");
        bytes4 signature;
        assembly {
            signature := mload(add(data, 32))
        }
        return signature;
    }
}

hardhat-deploy/solc_0.8/proxy/Proxied.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

abstract contract Proxied {
    /// @notice to be used by initialisation / postUpgrade function so that only the proxy's admin can execute them
    /// It also allows these functions to be called inside a contructor
    /// even if the contract is meant to be used without proxy
    modifier proxied() {
        address proxyAdminAddress = _proxyAdmin();
        // With hardhat-deploy proxies
        // the proxyAdminAddress is zero only for the implementation contract
        // if the implementation contract want to be used as a standalone/immutable contract
        // it simply has to execute the `proxied` function
        // This ensure the proxyAdminAddress is never zero post deployment
        // And allow you to keep the same code for both proxied contract and immutable contract
        if (proxyAdminAddress == address(0)) {
            // ensure can not be called twice when used outside of proxy : no admin
            // solhint-disable-next-line security/no-inline-assembly
            assembly {
                sstore(
                    0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103,
                    0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF
                )
            }
        } else {
            require(msg.sender == proxyAdminAddress);
        }
        _;
    }

    modifier onlyProxyAdmin() {
        require(msg.sender == _proxyAdmin(), "NOT_AUTHORIZED");
        _;
    }

    function _proxyAdmin() internal view returns (address ownerAddress) {
        // solhint-disable-next-line security/no-inline-assembly
        assembly {
            ownerAddress := sload(0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103)
        }
    }
}

